import React, { useEffect, useState } from 'react';
import { jwtDecode } from "jwt-decode"; 

const Dashboard = () => {
    const [username, setUsername] = useState('');

    useEffect(() => {
        const token = localStorage.getItem('access_token');
        if (token) {
            try {
                const decodedToken = jwtDecode(token); 
                console.log("Token decodificado:", decodedToken); // Ver qué contiene el token
                
                // Verifica qué propiedad tiene el username en el token
                if (decodedToken.username) {
                    setUsername(decodedToken.username);
                } else if (decodedToken.sub) {
                    setUsername(decodedToken.sub); // Algunas APIs usan 'sub'
                } else if (decodedToken.email) {
                    setUsername(decodedToken.email); // Si usa el email como identificador
                } else {
                    console.warn("No se encontró un campo adecuado para el nombre de usuario.");
                }

            } catch (error) {
                console.error('Error al decodificar el token:', error);
            }
        }
    }, []);

    return (
        <div className="container mt-5">
            <h1>Bienvenido, {username || 'Usuario'} </h1>
            <p>Aquí puedes gestionar tu inventario.</p>
        </div>
    );
};

export default Dashboard;
