import React, { useEffect, useState } from 'react';
import jwtDecode from "jwt-decode";

const fetchUser = async () => {
    const token = localStorage.getItem('access_token');
    if (!token) {
        console.error("No hay token disponible en localStorage");
        return;
    }

    try {
        const response = await fetch("http://127.0.0.1:8000/api/user/", {
            method: "GET",
            headers: {
                "Authorization": `Bearer ${token}`,
                "Content-Type": "application/json"
            }
        });

        if (!response.ok) {
            throw new Error(`Error ${response.status}: ${response.statusText}`);
        }

        const data = await response.json();
        setUsername(data.username);
    } catch (error) {
        console.error("Error al obtener usuario:", error);
    }
};

useEffect(() => {
    fetchUser();
}, []);

const Dashboard = () => {
    const [username, setUsername] = useState('');


    useEffect(() => {
        const token = localStorage.getItem('access_token');
        console.log("Token de acceso guardado:", token);

        if (!token) {
            console.error("No hay token disponible en localStorage");
            return;
        }

        try {
            const decodedToken = jwtDecode(token);
            console.log("Token decodificado:", decodedToken); // ðŸ‘€ Verifica si aparece el username
            setUsername(decodedToken.username || decodedToken.user_id); // Ajusta segÃºn tu API
        } catch (error) {
            console.error("Error al decodificar el token:", error);
        }
    }, []);  

    return (
        <div className="container mt-5">
            <h1>Bienvenido, {username ? username : 'Usuario'} </h1>
            <p>AquÃ­ puedes gestionar tu inventario.</p>
        </div>
    );
};

export default Dashboard;
