import React, { useEffect, useState } from 'react';
import { jwtDecode } from "jwt-decode"; 

const Dashboard = () => {
    const [username, setUsername] = useState('');

    useEffect(() => {
        const token = localStorage.getItem('access_token');
        if (token) {
            try {
                const decodedToken = jwtDecode(token);
                console.log("Token decodificado:", decodedToken); // üîç Ver qu√© contiene el token
                
                setUsername(JSON.stringify(decodedToken)); // Mostrar el contenido en pantalla (solo prueba)
                
            } catch (error) {
                console.error('Error al decodificar el token:', error);
            }
        }
    }, []);    

    return (
        <div className="container mt-5">
            <h1>Bienvenido, {username || 'Usuario'} </h1>
            <p>Aqu√≠ puedes gestionar tu inventario.</p>
        </div>
    );
};

export default Dashboard;
