import React, { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css';

const Navbar = ({ username, role, handleLogout }) => {
    return (
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark px-3">
            <Link className="navbar-brand" to="/dashboard">Inventario</Link>
            <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav">
                <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
                <ul className="navbar-nav me-auto">
                    <li className="nav-item">
                        <Link className="nav-link" to="/dashboard"><i className="bi bi-house-door me-2"></i>Dashboard</Link>
                    </li>
                    <li className="nav-item">
                        <Link className="nav-link" to="/productos"><i className="bi bi-box me-2"></i>Productos</Link>
                    </li>
                    <li className="nav-item">
                        <Link className="nav-link" to="/categorias"><i className="bi bi-tags me-2"></i>Categorías</Link>
                    </li>
                    <li className="nav-item">
                        <Link className="nav-link" to="/reportes"><i className="bi bi-graph-up me-2"></i>Reportes</Link>
                    </li>
                    {role === "admin" && (
                        <li className="nav-item">
                            <Link className="nav-link" to="/usuarios"><i className="bi bi-people me-2"></i>Usuarios</Link>
                        </li>
                    )}
                </ul>
                <span className="navbar-text me-3">{username || 'Usuario'} ({role || 'Invitado'})</span>
                <button onClick={handleLogout} className="btn btn-danger">
                    <i className="bi bi-box-arrow-right me-2"></i> Cerrar Sesión
                </button>
            </div>
        </nav>
    );
};

const Dashboard = () => {
    const [username, setUsername] = useState('');
    const [role, setRole] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('access_token');
        if (!token) {
            console.error("No hay token disponible");
            navigate('/');
            return;
        }
        const fetchUser = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:8000/user/', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setUsername(response.data.username);
                setRole(response.data.role);
            } catch (error) {
                console.error("Error al obtener usuario:", error);
                navigate('/');
            }
        };
        fetchUser();
    }, [navigate]);

    const handleLogout = () => {
        localStorage.removeItem('access_token');
        navigate('/');
    };

    return (
        <div>
            <Navbar username={username} role={role} handleLogout={handleLogout} />
            <div className="container mt-4">
                <h1 className="mb-4">Bienvenido, {username || 'Usuario'}</h1>
                <p className="text-muted">Aquí puedes gestionar tu inventario.</p>
                <div className="row">
                    <div className="col-md-4">
                        <div className="card bg-primary text-white text-center p-4">
                            <h5>Total Productos</h5>
                            <h2>152</h2>
                        </div>
                    </div>
                    <div className="col-md-4">
                        <div className="card bg-success text-white text-center p-4">
                            <h5>Stock Disponible</h5>
                            <h2>89%</h2>
                        </div>
                    </div>
                    <div className="col-md-4">
                        <div className="card bg-danger text-white text-center p-4">
                            <h5>Productos Agotados</h5>
                            <h2>12</h2>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
