import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Importamos useNavigate
import axios from 'axios';

import { Link } from "react-router-dom"; // Usamos Link de React Router

const Sidebar = ({ role, handleLogout }) => {
  return (
    <aside className="d-flex flex-column bg-dark text-white vh-100 p-4">
      {/* Título */}
      <h2 className="text-center mb-4">Inventario</h2>

      {/* Lista de navegación */}
      <ul className="nav flex-column mb-auto">
        <li className="nav-item">
          <Link to="/dashboard" className="nav-link text-white">
            <i className="bi bi-house-door me-2"></i> Dashboard
          </Link>
        </li>
        <li className="nav-item">
          <Link to="/productos" className="nav-link text-white">
            <i className="bi bi-box me-2"></i> Productos
          </Link>
        </li>
        <li className="nav-item">
          <Link to="/categorias" className="nav-link text-white">
            <i className="bi bi-tags me-2"></i> Categorías
          </Link>
        </li>
        <li className="nav-item">
          <Link to="/reportes" className="nav-link text-white">
            <i className="bi bi-graph-up me-2"></i> Reportes
          </Link>
        </li>
        {role === "admin" && (
          <li className="nav-item">
            <Link to="/usuarios" className="nav-link text-white">
              <i className="bi bi-people me-2"></i> Usuarios
            </Link>
          </li>
        )}
      </ul>

      {/* Botón de Cerrar Sesión */}
      <button onClick={handleLogout} className="btn btn-danger w-100 mt-auto">
        <i className="bi bi-box-arrow-right me-2"></i> Cerrar Sesión
      </button>
    </aside>
  );
};

const Dashboard = () => {
    const [username, setUsername] = useState('');
    const [role, setRole] = useState('');
    const navigate = useNavigate(); // Hook para redirección

    useEffect(() => {
        const token = localStorage.getItem('access_token');

        // Si no hay token, redirigir al login
        if (!token) {
            console.error("No hay token disponible");
            navigate('/'); // Redirigir a la página de login
            return;
        }

        const fetchUser = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:8000/user/', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setUsername(response.data.username);
                setRole(response.data.role);
            } catch (error) {
                console.error("Error al obtener usuario:", error);
                navigate('/'); // Si falla la petición, redirigir al login
            }
        };

        fetchUser();
    }, [navigate]);

    // Función para cerrar sesión
    const handleLogout = () => {
        localStorage.removeItem('access_token'); // Eliminar token
        navigate('/'); // Redirigir al login
    };

    return (
        <div className="flex h-screen">
            <Sidebar role={role} handleLogout={handleLogout} />
            <main className="flex-1 p-10">
                <h1 className="text-3xl font-bold">Bienvenido, {username || 'Usuario'} - Rol: {role || 'Usuario'}</h1>
                <p className="text-gray-600 mt-2">Aquí puedes gestionar tu inventario.</p>

                <div className="grid grid-cols-3 gap-6 mt-6">
                    <div className="bg-blue-500 p-5 text-white rounded-lg shadow-md">
                        <h2 className="text-lg font-semibold">Total Productos</h2>
                        <p className="text-2xl font-bold">152</p>
                    </div>
                    <div className="bg-green-500 p-5 text-white rounded-lg shadow-md">
                        <h2 className="text-lg font-semibold">Stock Disponible</h2>
                        <p className="text-2xl font-bold">89%</p>
                    </div>
                    <div className="bg-red-500 p-5 text-white rounded-lg shadow-md">
                        <h2 className="text-lg font-semibold">Productos Agotados</h2>
                        <p className="text-2xl font-bold">12</p>
                    </div>
                </div>
            </main>
        </div>
    );
};

export default Dashboard;
